import BAC0

def make_pc_as_bacnet_ip_device():
    # Create a BACnet device with a unique identifier
    device_id = 123
    bacnet_device = BAC0.bacnet_device(device_id)

    # Set the device name
    bacnet_device.name = "MyBACnetDevice"

    # Set the device description
    bacnet_device.description = "BACnet/IP Device on PC"

    # Add analog input objects (AnalogInput, AnalogValue)
    bacnet_device.add_object_list("analogInput", range(1, 6))

    # Start the BACnet device
    bacnet_device.start()

    print(f"BACnet/IP device with ID {device_id} is now running.")

if __name__ == "__main__":
    make_pc_as_bacnet_ip_device()

